import React, { FC, useState } from "react";
import "./App.css";
import Statistic from "./components/Statistic";
import TodoList from "./components/TodoList";
import AddTodo from "./components/AddTodo";
import { TodoData } from "./TodoData";

const App: FC = () => {
  const [todos, setTodos] = useState<TodoData[]>([
    {
      id: 1,
      name: "todo 1",
      done: true,
    },
    {
      id: 2,
      name: "todo 2",
      done: false,
    },
    {
      id: 3,
      name: "todo 3",
      done: false,
    },
    {
      id: 4,
      name: "todo 4",
      done: false,
    },
    {
      id: 5,
      name: "todo 5",
      done: true,
    },
  ]);

  return (
    <div>
      <Statistic
        done={todos.filter((todo) => todo.done).length}
        total={todos.length}
      />

      <TodoList
        todos={todos}
        onTodoUpdate={(id, done) => {
          const newUpdateTodos = todos.map((todo) => {
            if (todo.id === id) {
              return {
                ...todo,
                done,
              }
            }
            return todo
          })
          setTodos(newUpdateTodos)
        }}
      />

      <button>clear</button>

      <AddTodo
        onNewTodo={(todoName) => {
          setTodos([
            ...todos,
            { name: todoName, done: false, id: new Date().getTime() },
          ]);
        }}
      />
    </div>
  );
};

export default App;
